import React, { Component } from "react";
import Card from "./components/Card";
import Wrapper from "./components/Wrapper";
import Score from "./components/Score";
import API from "./utils/API";
import "./App.css";

class App extends Component {
  // Setting this.state.pups to the cards json array
  state = {
    pups: [],
    puppyIds: [],
    score: 0,
    topScore: 0,
  };

  componentDidMount() {
    API.getPuppyList()
      .then((res) => {
        console.log(res.data.message);
        this.setState({ pups: res.data.message });
      })
      .catch((err) => console.log(err));
  }

  newScore = this.state.score;
  clickedPuppyIds = this.state.puppyIds;
  topScore = this.state.topScore;

  shuffleScoreCard = (id) => {
    this.clickedPuppyIds = this.state.puppyIds;
    if (this.clickedPuppyIds.some((pupId) => pupId === id)) {
      this.newScore = 0;
      this.clickedPuppyIds = [];
    } else {
      this.newScore++;
      this.clickedPuppyIds.push(id);
      if (this.newScore > this.topScore) this.topScore = this.newScore;
    }
    this.setState((state) => ({
      puppyIds: this.clickedPuppyIds,
      score: this.newScore,
      topScore: this.topScore,
    }));
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="App-title">The Clickster</h1>
          <p className="App-intro">Try not to click the same image twice!</p>
        </header>

        <Score score={this.state.score} topScore={this.state.topScore} />

      </div>
    );
  }
  //}
}

export default App;
