import React, { Component } from "react";
import Card from "./components/Card";
import Wrapper from "./components/Wrapper";
import Score from "./components/Score";
import pups from "./cards.json";
import "./App.css";


class App extends Component {
  // Setting this.state.pups to the cards json array
  state = {
    search: "",
    pups,
    clickedPuppyIds: [],
    score: 0,
    goal: 8,
    status: ""
  };

  shuffle(array) {
    var currentIndex = array.length, temporaryValue, randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;

      // And swap it with the current element.
      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }

    return array;
  }

  randomPups = this.shuffle(this.state.pups);

  shuffleScoreCard = (id) => {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
    this.state.clickedPuppyIds.push(id);
    this.randomPups = this.shuffle(this.state.pups);
    console.log('Puuppy clicked id');
    console.log(this.state.clickedPuppyIds);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="App-title">The Clickster</h1>
          <p className="App-intro">
            Try not to click the same image twice!
          </p>
        </header>

        <Score total={this.state.score}
           goal={8}
            status={this.state.status}
        />
        <Wrapper>
          {this.randomPups.map(puppy => (
            <Card
              shuffleScoreCard={this.shuffleScoreCard}
              id={puppy.id}
              key={puppy.id}
              image={puppy.image}
            />
          ))}
        </Wrapper>

    </div>
    );
  }
//}
}

export default App;
